import styled from 'styled-components';
import { motion } from 'framer-motion';

export const FeatureColumn = styled(motion.div)`
	display: flex;
	flex-flow: column;
	justify-content: center;
	align-items: center;
	background: #f3f3f3;
	padding: 10px;
	box-shadow: 0 0 12px 8px #d0d0d0;
	border-radius: 10px;
`;
